import Layout from '../../components/Layout';

const Post = (props) => {
  const router = useRouter();

  return (
    <Layout>
      <h1>{props.show.name}</h1>
      <p>{props.show.summary.replace(/<[/]?[pb]>/g, '')}</p>
      {props.show.image ? <img src={props.show.image.medium} /> : null}
    </Layout>
  );
}

export default Post;
